      <div :class="`status-${status.toLowerCase()}`" class="mr-5" />
      <div v-if="status.toLowerCase() == 'accepted'" class="space-y-1">
        <div class="font-semibold text-gray-800">Awaiting payment</div>
        <div class="text-sm text-gray-600">In the near future, the agent will transfer funds according to your details</div>
      </div>
      <div v-if="status.toLowerCase() == 'cashed'" class="space-y-1">
        <div class="font-semibold text-gray-800">Payment has been sent</div>
        <div class="text-sm text-gray-600">Agent marked payment as sent. Check receipt and confirm</div>
      </div>
      <div v-if="status.toLowerCase() == 'disputed'" class="space-y-1">
        <div class="font-semibold text-gray-800">Under review</div>
        <div class="text-sm text-gray-600">The agent will confirm the payment as soon as it checks the receipt of money</div>
      </div>
      <div v-if="status.toLowerCase() == 'canceled'" class="space-y-1">
        <div class="font-semibold text-gray-800">Cancelled</div>
        <div class="text-sm text-gray-600">Payment was canceled by one of the parties or timed out</div>
      </div>
      <div v-if="status.toLowerCase() == 'confirmed'" class="space-y-1">
        <div class="font-semibold text-gray-800">Confirmed</div>
        <div class="text-sm text-gray-600">Withdrawal request successfully completed</div>
      </div>
      <div v-if="status.toLowerCase() == 'declined'" class="space-y-1">
        <div class="font-semibold text-gray-800">Unconfirmed</div>
        <div class="text-sm text-gray-600">Unfortunately, the agent could not find and confirm your payment</div>
      </div>
      <div v-if="status.toLowerCase() == 'rejected'" class="space-y-1">
        <div class="font-semibold text-gray-800">Not confirmed</div>
        <div class="text-sm text-gray-600">Unfortunately, the agent could not find or confirm your payment</div>
      </div>
    </div>
  </div>
</template>

<script setup lang="ts">
defineProps<{
  status: string
}>()
</script>

